--card owner: saikufunction c11227781.initial_effect(c)	--pendulum summon	aux.EnablePendulumAttribute(c)	--spsummon	local e1=Effect.CreateEffect(c)	e1:SetDescription(aux.Stringid(11227781,0))	e1:SetCategory(CATEGORY_SPECIAL_SUMMON)	e1:SetType(EFFECT_TYPE_IGNITION)	e1:SetRange(LOCATION_PZONE)	e1:SetCountLimit(1,11227781)	e1:SetCondition(c11227781.spcon)	e1:SetTarget(c11227781.sptg)	e1:SetOperation(c11227781.spop)	c:RegisterEffect(e1)	--destroy	local e2=Effect.CreateEffect(c)	e2:SetDescription(aux.Stringid(11227781,1))	e2:SetCategory(CATEGORY_DESTROY)	e2:SetType(EFFECT_TYPE_IGNITION)	e2:SetProperty(EFFECT_FLAG_CARD_TARGET)	e2:SetCountLimit(1)	e2:SetRange(LOCATION_PZONE)	e2:SetCost(c11227781.cost)	e2:SetTarget(c11227781.target)	e2:SetOperation(c11227781.operation)	c:RegisterEffect(e2)	--directatt	local e3=Effect.CreateEffect(c)	e3:SetType(EFFECT_TYPE_SINGLE)	e3:SetCode(EFFECT_DIRECT_ATTACK)	c:RegisterEffect(e3)endfunction c11227781.spcon(e,tp,eg,ep,ev,re,r,rp)	returnDuel.GetFieldGroupCount(tp,LOCATION_MZONE,0)==0endfunctionc11227781.sptg(e,tp,eg,ep,ev,re,r,rp,chk)	local c=e:GetHandler()	if chk==0 then return Duel.GetLocationCount(tp,LOCATION_MZONE)>0		andc:IsCanBeSpecialSummoned(e,0,tp,false,false)end	Duel.SetOperationInfo(0,CATEGORY_SPECIAL_SUMMON,c,1,0,0)endfunction c11227781.spop(e,tp,eg,ep,ev,re,r,rp)	local c=e:GetHandler()	if c:IsRelateToEffect(e) then		Duel.SpecialSummon(c,0,tp,tp,false,false,POS_FACEUP)	endendfunction c11227781.atkcon(e)	local c=e:GetHandler()	local tp=e:GetHandlerPlayer()	returnDuel.GetMatchingGroupCount(nil,tp,LOCATION_MZONE,0,c)==0endfunction c11227781.costfilter(c)	return c:IsFaceup() and c:IsSetCard(0x26E7)endfunction c11227781.cost(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.CheckReleaseGroup(tp,c11227781.costfilter,1,e:GetHandler()) end	local sg=Duel.SelectReleaseGroup(tp,c11227781.costfilter,1,1,e:GetHandler())	Duel.Release(sg,REASON_COST)endfunction c11227781.target(e,tp,eg,ep,ev,re,r,rp,chk,chkc)	if chkc then return chkc:IsControler(1-tp) and chkc:IsOnField() end	if chk==0 then return Duel.IsExistingTarget(aux.TRUE,tp,0,LOCATION_ONFIELD,1,nil) end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_DESTROY)	local g=Duel.SelectTarget(tp,aux.TRUE,tp,0,LOCATION_ONFIELD,1,1,nil)	Duel.SetOperationInfo(0,CATEGORY_DESTROY,g,1,0,0)endfunction c11227781.operation(e,tp,eg,ep,ev,re,r,rp)	local tc=Duel.GetFirstTarget()	if tc and tc:IsRelateToEffect(e) then		Duel.Destroy(tc,REASON_EFFECT)	endend