--Card owner: saikufunction c11227787.initial_effect(c)	--Activate	local e1=Effect.CreateEffect(c)	e1:SetCategory(CATEGORY_SPECIAL_SUMMON)	e1:SetType(EFFECT_TYPE_ACTIVATE)	e1:SetCode(EVENT_FREE_CHAIN)	e1:SetTarget(c11227787.target)       e1:SetCountLimit(1,11227787)	e1:SetOperation(c11227787.activate)	c:RegisterEffect(e1)	--SPELL/TRAP  hand	local e2=Effect.CreateEffect(c)	e2:SetCategory(CATEGORY_TOHAND)	e2:SetType(EFFECT_TYPE_IGNITION)	e2:SetProperty(EFFECT_FLAG_CARD_TARGET)	e2:SetRange(LOCATION_GRAVE)	e2:SetCountLimit(1,11227788)	e2:SetCost(aux.bfgcost)	e2:SetTarget(c11227787.thtg)	e2:SetOperation(c11227787.thop)	c:RegisterEffect(e2)endfunction c11227787.filter(c,e,tp)	return c:IsSetCard(0x26E7)endfunction c11227787.target(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.GetLocationCount(tp,LOCATION_MZONE)>0		and Duel.IsExistingMatchingCard(c11227787.filter,tp,LOCATION_DECK,0,1,nil,e,tp) end	Duel.SetOperationInfo(0,CATEGORY_SPECIAL_SUMMON,nil,1,tp,LOCATION_DECK)endfunction c11227787.dfilter(c)	return c:IsFaceup() and c:IsSetCard(0x26E7)endfunction c11227787.activate(e,tp,eg,ep,ev,re,r,rp)	if Duel.GetLocationCount(tp,LOCATION_MZONE)>0 then		Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_SPSUMMON)		local g=Duel.SelectMatchingCard(tp,c11227787.filter,tp,LOCATION_DECK,0,1,1,nil,e,tp)		if g:GetCount()>0 then			Duel.SpecialSummon(g,0,tp,tp,false,false,POS_FACEUP)		end	endendfunction c11227787.thfilter(c)	return c:IsSetCard(0x26E7) and c:IsType(TYPE_SPELL+TYPE_TRAP) and c:IsAbleToHand()endfunction c11227787.thtg(e,tp,eg,ep,ev,re,r,rp,chk,chkc)	if chkc then return chkc:IsControler(tp) and chkc:IsLocation(LOCATION_GRAVE) and c11227787.thfilter(chkc) end	if chk==0 then return Duel.IsExistingTarget(c11227787.thfilter,tp,LOCATION_GRAVE,0,1,nil) end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_ATOHAND)	local g=Duel.SelectTarget(tp,c11227787.thfilter,tp,LOCATION_GRAVE,0,1,1,nil)	Duel.SetOperationInfo(0,CATEGORY_TOHAND,g,1,0,0)endfunction c11227787.thop(e,tp,eg,ep,ev,re,r,rp)	local tc=Duel.GetFirstTarget()	if tc:IsRelateToEffect(e) then		Duel.SendtoHand(tc,nil,REASON_EFFECT)	endend