--card owner:saikulocal s,id=GetID()function s.initial_effect(c)	--activate	local e1=Effect.CreateEffect(c)	e1:SetType(EFFECT_TYPE_ACTIVATE)	e1:SetCode(EVENT_FREE_CHAIN)	c:RegisterEffect(e1)	--leavethefield	local e5=Effect.CreateEffect(c)	e5:SetCategory(CATEGORY_TOHAND)	e5:SetType(EFFECT_TYPE_FIELD+EFFECT_TYPE_TRIGGER_O)	e5:SetCode(EVENT_LEAVE_FIELD)	e5:SetProperty(EFFECT_FLAG_CARD_TARGET+EFFECT_FLAG_DAMAGE_STEP+EFFECT_FLAG_DELAY)	e5:SetRange(LOCATION_SZONE)	e5:SetCountLimit(1)	e5:SetTarget(s.thtg)	e5:SetOperation(s.thop)	c:RegisterEffect(e5)	--tohand	local e6=Effect.CreateEffect(c)	e6:SetCategory(CATEGORY_TOHAND)	e6:SetType(EFFECT_TYPE_IGNITION)	e6:SetRange(LOCATION_SZONE)       e6:SetCountLimit(1)	e6:SetTarget(s.tttg)	e6:SetOperation(s.toop)	c:RegisterEffect(e6)endfunction s.reltg(e,c)	return c:IsFaceup() and c:IsType(TYPE_PENDULUM)endfunction s.sumlimit(e,c)	if not c then return false end	return not c:IsControler(e:GetHandlerPlayer())endfunction s.esumlimit(re,rp,c)	if not c then return false end	return not c:IsControler(rp)endfunction s.thfilter(c,tp)	return c:IsType(TYPE_PENDULUM) and c:IsSetCard(0x26E7) and c:IsControler(tp) 		and ((c:IsLocation(LOCATION_EXTRA) and c:IsFaceup()) or not c:IsLocation(LOCATION_DECK))		and c:GetPreviousControler()==tp and c:IsAbleToHand()endfunction s.thtg(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return eg and eg:IsExists(s.thfilter,1,nil,tp) end	Duel.SetOperationInfo(0,CATEGORY_TOHAND,eg,1,0,0)endfunction s.thop(e,tp,eg,ep,ev,re,r,rp)	if not e:GetHandler():IsRelateToEffect(e) then return end	local g=eg:Filter(s.thfilter,nil,tp)	if g:GetCount()==0 then return end	local tc=nil	if g:GetCount()==1 then		tc=g:GetFirst()	else		Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_SPSUMMON)		tc=g:Select(tp,1,1,nil):GetFirst()	end	Duel.ConfirmCards(1-tp,tc)	Duel.SendtoHand(tc,nil,REASON_EFFECT)endfunction s.tffilter(c)	return c:IsFaceup() and c:IsSetCard(0x26E7) and c:IsType(TYPE_PENDULUM) and c:IsAbleToHand()endfunction s.tttg(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.IsExistingMatchingCard(s.tffilter,tp,LOCATION_EXTRA,0,1,nil) end	Duel.SetOperationInfo(0,CATEGORY_TOHAND,nil,1,tp,LOCATION_EXTRA)endfunction s.toop(e,tp,eg,ep,ev,re,r,rp)	if not e:GetHandler():IsRelateToEffect(e) then return end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_ATOHAND)	local g=Duel.SelectMatchingCard(tp,s.tffilter,tp,LOCATION_EXTRA,0,1,1,nil)	if g:GetCount()>0 then		Duel.SendtoHand(g,nil,REASON_EFFECT)		Duel.ConfirmCards(1-tp,g)	endend