--card owner:saikulocal s,id=GetID()function s.initial_effect(c)	--activate	local e1=Effect.CreateEffect(c)	e1:SetType(EFFECT_TYPE_ACTIVATE)	e1:SetCode(EVENT_FREE_CHAIN)	c:RegisterEffect(e1)	--leavethefield	local e5=Effect.CreateEffect(c)	e5:SetCategory(CATEGORY_TOHAND)	e5:SetType(EFFECT_TYPE_FIELD+EFFECT_TYPE_TRIGGER_O)	e5:SetCode(EVENT_LEAVE_FIELD)	e5:SetProperty(EFFECT_FLAG_DELAY)	e5:SetRange(LOCATION_SZONE)	e5:SetCountLimit(1)	e5:SetTarget(s.thtg)	e5:SetOperation(s.thop)	c:RegisterEffect(e5)	--draw	local e6=Effect.CreateEffect(c)	e6:SetCategory(CATEGORY_DRAW)	e6:SetType(EFFECT_TYPE_IGNITION)	e6:SetRange(LOCATION_SZONE)	e6:SetCode(EVENT_FREE_CHAIN)	e6:SetProperty(EFFECT_FLAG_PLAYER_TARGET)	e6:SetCountLimit(1,11227789)	e6:SetCost(s.cost)	e6:SetTarget(s.target)	e6:SetOperation(s.activate)	c:RegisterEffect(e6)endfunction s.reltg(e,c)	return c:IsFaceup() and c:IsType(TYPE_PENDULUM)endfunction s.sumlimit(e,c)	if not c then return false end	return not c:IsControler(e:GetHandlerPlayer())endfunction s.esumlimit(re,rp,c)	if not c then return false end	return not c:IsControler(rp)endfunction s.thfilter(c,tp)	return c:IsType(TYPE_PENDULUM) and c:IsControler(tp) 		and ((c:IsLocation(LOCATION_EXTRA) and c:IsFaceup()) or not c:IsLocation(LOCATION_DECK))		and c:GetPreviousControler()==tp and c:IsAbleToHand()endfunction s.thtg(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return eg and eg:IsExists(s.thfilter,1,nil,tp) end	Duel.SetOperationInfo(0,CATEGORY_TOHAND,eg,1,0,0)endfunction s.thop(e,tp,eg,ep,ev,re,r,rp)	if not e:GetHandler():IsRelateToEffect(e) then return end	local g=eg:Filter(s.thfilter,nil,tp)	if g:GetCount()==0 then return end	local tc=nil	if g:GetCount()==1 then		tc=g:GetFirst()	else		Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_SPSUMMON)		tc=g:Select(tp,1,1,nil):GetFirst()	end	Duel.ConfirmCards(1-tp,tc)	Duel.SendtoHand(tc,nil,REASON_EFFECT)endfunction s.cfilter(c)	return c:IsSetCard(0x26E7) and c:IsAbleToHandAsCost()endfunction s.cost(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.IsExistingMatchingCard(s.cfilter,tp,LOCATION_EXTRA,0,1,nil) end	Duel.SendtoHand(tp,s.cfilter,1,1,REASON_COST,nil)endfunction s.target(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.IsPlayerCanDraw(tp,1) end	Duel.SetTargetPlayer(tp)	Duel.SetTargetParam(1)	Duel.SetOperationInfo(0,CATEGORY_DRAW,nil,0,tp,1)endfunction s.tgfilter(c)	return c:IsSetCard(0x26E7) and c:IsAbleToHand()endfunction s.activate(e,tp,eg,ep,ev,re,r,rp)	local p,d=Duel.GetChainInfo(0,CHAININFO_TARGET_PLAYER,CHAININFO_TARGET_PARAM)	local dr=Duel.Draw(p,d,REASON_EFFECT)	local g=Duel.GetMatchingGroup(s.tgfilter,p,LOCATION_EXTRA,0,nil)	if dr~=0 and g:GetCount()>0 and Duel.SelectYesNo(p,aux.Stringid(s,0)) then		Duel.BreakEffect()		Duel.Hint(HINT_SELECTMSG,p,HINTMSG_TOHAND)		local sg=g:Select(p,1,1,nil)		Duel.SendtoHand(sg,REASON_EFFECT)	endend