--card owner : saikulocal m=11227801local cm=_G["c"..m]function cm.initial_effect(c)    --deckTOxtradeck    local e1=Effect.CreateEffect(c)  e1:SetCategory(CATEGORY_DRAW+CATEGORY_RECOVER)    e1:SetType(EFFECT_TYPE_ACTIVATE)    e1:SetCode(EVENT_FREE_CHAIN)       e1:SetTarget(cm.tetg)    e1:SetOperation(cm.teop)    c:RegisterEffect(e1)    --xtraTOhand    local e2=Effect.CreateEffect(c)    e2:SetCategory(CATEGORY_TOHAND)    e2:SetType(EFFECT_TYPE_IGNITION)    e2:SetRange(LOCATION_GRAVE)    e2:SetCost(aux.bfgcost)    e2:SetTarget(cm.thtg)    e2:SetOperation(cm.thop)    c:RegisterEffect(e2)endfunction cm.tefilter(c)    return c:IsType(TYPE_PENDULUM) and not c:IsForbidden() and c:IsSetCard(0x26E7)endfunction cm.tetg(e,tp,eg,ep,ev,re,r,rp,chk)    if chk==0 then return Duel.IsExistingMatchingCard(cm.tefilter,tp,LOCATION_DECK,0,1,nil) end    Duel.SetOperationInfo(0,CATEGORY_TOEXTRA,nil,1,tp,LOCATION_DECK)endfunction cm.teop(e,tp,eg,ep,ev,re,r,rp)    Duel.Hint(HINT_SELECTMSG,tp,aux.Stringid(m,3))    local g=Duel.SelectMatchingCard(tp,cm.tefilter,tp,LOCATION_DECK,0,1,1,nil)    if g:GetCount()>0 then        Duel.SendtoExtraP(g,tp,REASON_EFFECT)    endendfunction cm.thfilter(c)    return c:IsSetCard(0x26E7) and c:IsType(TYPE_PENDULUM) and c:IsAbleToHand()endfunction cm.thtg(e,tp,eg,ep,ev,re,r,rp,chk)    if chk==0 then return Duel.IsExistingMatchingCard(cm.thfilter,tp,LOCATION_EXTRA,0,1,nil) end    Duel.SetOperationInfo(0,CATEGORY_TOHAND,nil,1,tp,LOCATION_EXTRA)endfunction cm.thop(e,tp,eg,ep,ev,re,r,rp)    Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_ATOHAND)    local g=Duel.SelectMatchingCard(tp,cm.thfilter,tp,LOCATION_EXTRA,0,1,1,nil)    if g:GetCount()>0 then        Duel.SendtoHand(g,nil,REASON_EFFECT)        Duel.ConfirmCards(1-tp,g)    endend