--Jovial Shuffler (scripter: Elmaâœ“)function c12593459.initial_effect(c)        --special summon	local e1=Effect.CreateEffect(c)	e1:SetType(EFFECT_TYPE_FIELD)	e1:SetCode(EFFECT_SPSUMMON_PROC)	e1:SetProperty(EFFECT_FLAG_UNCOPYABLE)	e1:SetRange(LOCATION_HAND)	e1:SetCondition(c12593459.spcon)	c:RegisterEffect(e1)	--to deck	local e2=Effect.CreateEffect(c)	e2:SetCategory(CATEGORY_TODECK+CATEGORY_TOHAND)	e2:SetType(EFFECT_TYPE_IGNITION)	e2:SetRange(LOCATION_GRAVE)	e2:SetProperty(EFFECT_FLAG_CARD_TARGET)	e2:SetCountLimit(1,12593459)	e2:SetTarget(c12593459.tdtg)	e2:SetOperation(c12593459.tdop)	c:RegisterEffect(e2)endfunction c12593459.filter(c)	return c:IsFaceup() and c:IsSetCard(0x2EF) and c:GetCode()~=12593459endfunction c12593459.spcon(e,c)	if c==nil then return true end	return Duel.GetLocationCount(c:GetControler(),LOCATION_MZONE)>0 and		Duel.IsExistingMatchingCard(c12593459.filter,c:GetControler(),LOCATION_MZONE,0,1,nil)endfunction c12593459.tdfilter(c)	return c:IsSetCard(0x2EF) and c:IsAbleToDeck()endfunction c12593459.tdtg(e,tp,eg,ep,ev,re,r,rp,chk,chkc)	if chkc then return chkc:IsLocation(LOCATION_GRAVE) and chkc:IsControler(tp) and c12593459.tdfilter(chkc) end	if chk==0 then return e:GetHandler():IsAbleToHand()		and Duel.IsExistingTarget(c12593459.tdfilter,tp,LOCATION_GRAVE,0,1,nil) end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_TODECK)	local g=Duel.SelectTarget(tp,c12593459.tdfilter,tp,LOCATION_GRAVE,0,1,1,nil)	Duel.SetOperationInfo(0,CATEGORY_TODECK,g,1,0,0)	Duel.SetOperationInfo(0,CATEGORY_TOHAND,e:GetHandler(),1,0,0)endfunction c12593459.tdop(e,tp,eg,ep,ev,re,r,rp)	local tc=Duel.GetFirstTarget()	local c=e:GetHandler()	if tc:IsRelateToEffect(e) and Duel.SendtoDeck(tc,nil,2,REASON_EFFECT)~=0		and tc:IsLocation(LOCATION_DECK+LOCATION_EXTRA) and c:IsRelateToEffect(e) then		Duel.SendtoHand(c,nil,REASON_EFFECT)	endend