--Jovial Juggler #one (scripter: Elmaâœ“)function c12593461.initial_effect(c)      --token	local e1=Effect.CreateEffect(c)	e1:SetDescription(aux.Stringid(12593461,0))	e1:SetCategory(CATEGORY_SPECIAL_SUMMON+CATEGORY_TOKEN)	e1:SetType(EFFECT_TYPE_SINGLE+EFFECT_TYPE_TRIGGER_F)	e1:SetCode(EVENT_LEAVE_FIELD)	e1:SetCondition(c12593461.tkcon)	e1:SetCountLimit(1,12593461)	e1:SetTarget(c12593461.tktg)	e1:SetOperation(c12593461.tkop)	c:RegisterEffect(e1)endfunction c12593461.tkcon(e,tp,eg,ep,ev,re,r,rp)	local c=e:GetHandler()	return c:IsPreviousPosition(POS_FACEUP)endfunction c12593461.tktg(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return true end	Duel.SetOperationInfo(0,CATEGORY_TOKEN,nil,1,0,0)	Duel.SetOperationInfo(0,CATEGORY_SPECIAL_SUMMON,nil,1,0,0)endfunction c12593461.tkop(e,tp,eg,ep,ev,re,r,rp)	if Duel.GetLocationCount(tp,LOCATION_MZONE)<=0 then return end	if Duel.IsPlayerCanSpecialSummonMonster(tp,12593463,0,0x2F,1200,1900,3,RACE_FIEND,ATTRIBUTE_DARK) then		local token=Duel.CreateToken(tp,12593463)		Duel.SpecialSummon(token,0,tp,tp,false,false,POS_FACEUP)	endendfunction c12593461.cfilter(c,ft,tp)	return ft>0 or (c:IsControler(tp) and c:GetSequence()<5)end