--Aghast Fire Eater (scripter: Elmaâœ“)function c12593469.initial_effect(c)	--synchro summon	aux.AddSynchroProcedure(c,aux.FilterBoolFunction(Card.IsCode,12593467),aux.NonTuner(Card.IsSetCard,0x2EF),1)	c:EnableReviveLimit()       --equip	local e1=Effect.CreateEffect(c)	e1:SetDescription(aux.Stringid(12593469,1))	e1:SetCategory(CATEGORY_EQUIP)	e1:SetType(EFFECT_TYPE_SINGLE+EFFECT_TYPE_TRIGGER_O)	e1:SetCode(EVENT_BATTLE_START)	e1:SetCountLimit(1,12593470)	e1:SetCondition(c12593469.eqcon)	e1:SetTarget(c12593469.eqtg)	e1:SetOperation(c12593469.eqop)	c:RegisterEffect(e1)       --special summon	local e2=Effect.CreateEffect(c)	e2:SetDescription(aux.Stringid(12593469,2))	e2:SetCategory(CATEGORY_SPECIAL_SUMMON)	e2:SetType(EFFECT_TYPE_IGNITION)	e2:SetProperty(EFFECT_FLAG_CARD_TARGET)	e2:SetRange(LOCATION_MZONE)	e2:SetCountLimit(1)	e2:SetCost(c12593469.spcost)	e2:SetTarget(c12593469.sptg)	e2:SetOperation(c12593469.spop)	c:RegisterEffect(e2)endfunction c12593469.eqcon(e,tp,eg,ep,ev,re,r,rp)	local ec=e:GetLabelObject()	return Duel.GetAttacker()==e:GetHandler() and (ec==nil or ec:GetFlagEffect(12593469)==0)endfunction c12593469.eqtg(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.GetLocationCount(tp,LOCATION_SZONE)>0		and Duel.IsExistingMatchingCard(Card.IsFaceup,tp,LOCATION_MZONE,LOCATION_MZONE,1,e:GetHandler()) endendfunction c12593469.eqop(e,tp,eg,ep,ev,re,r,rp)	local c=e:GetHandler()	if Duel.GetLocationCount(tp,LOCATION_SZONE)<=0 then return end	if c:IsFacedown() or not c:IsRelateToEffect(e) then return end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_EQUIP)	local g=Duel.SelectMatchingCard(tp,Card.IsFaceup,tp,LOCATION_MZONE,LOCATION_MZONE,1,1,aux.ExceptThisCard(e))	local tc=g:GetFirst()	if tc then		if not Duel.Equip(tp,tc,c) then return end		tc:RegisterFlagEffect(12593469,RESET_EVENT+RESETS_STANDARD,0,0)		e:SetLabelObject(tc)		local e1=Effect.CreateEffect(c)		e1:SetType(EFFECT_TYPE_SINGLE)		e1:SetProperty(EFFECT_FLAG_COPY_INHERIT+EFFECT_FLAG_OWNER_RELATE)		e1:SetCode(EFFECT_EQUIP_LIMIT)		e1:SetValue(c12593469.eqlimit)		e1:SetReset(RESET_EVENT+RESETS_STANDARD)		tc:RegisterEffect(e1)		local e2=Effect.CreateEffect(c)		e2:SetType(EFFECT_TYPE_EQUIP)		e2:SetCode(EFFECT_UPDATE_ATTACK)		e2:SetValue(1000)		e2:SetReset(RESET_EVENT+RESETS_STANDARD)		tc:RegisterEffect(e2)	endendfunction c12593469.eqlimit(e,c)	return e:GetOwner()==cendfunction c12593469.spcost(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return e:GetHandler():GetEquipGroup():IsExists(Card.IsAbleToGraveAsCost,1,nil) end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_TOGRAVE)	local g=e:GetHandler():GetEquipGroup():FilterSelect(tp,Card.IsAbleToGraveAsCost,1,1,nil)	Duel.SendtoGrave(g,REASON_COST)endfunction c12593469.spfilter(c,e,tp)	return c:IsSetCard(0x2EF) and c:IsCanBeSpecialSummoned(e,0,tp,false,false)endfunction c12593469.sptg(e,tp,eg,ep,ev,re,r,rp,chk,chkc)	if chkc then return chkc:IsControler(tp) and chkc:IsLocation(LOCATION_GRAVE) and c12593469.spfilter(chkc,e,tp) end	if chk==0 then return Duel.GetLocationCount(tp,LOCATION_MZONE)>0		and Duel.IsExistingTarget(c12593469.spfilter,tp,LOCATION_GRAVE,0,1,nil,e,tp) end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_SPSUMMON)	local g=Duel.SelectTarget(tp,c12593469.spfilter,tp,LOCATION_GRAVE,0,1,1,nil,e,tp)	Duel.SetOperationInfo(0,CATEGORY_SPECIAL_SUMMON,g,1,0,0)endfunction c12593469.spop(e,tp,eg,ep,ev,re,r,rp)	local tc=Duel.GetFirstTarget()	if tc:IsRelateToEffect(e) then		Duel.SpecialSummon(tc,0,tp,tp,false,false,POS_FACEUP)	endend