--Jovial Bard (scripter: Elmaâœ“)function c12593471.initial_effect(c)	--equip	local e1=Effect.CreateEffect(c)	e1:SetCategory(CATEGORY_EQUIP)	e1:SetType(EFFECT_TYPE_QUICK_O)	e1:SetProperty(EFFECT_FLAG_CARD_TARGET+EFFECT_FLAG_DAMAGE_STEP)	e1:SetRange(LOCATION_HAND+LOCATION_MZONE)	e1:SetCode(EVENT_FREE_CHAIN)	e1:SetCondition(c12593471.eqcon)	e1:SetTarget(c12593471.eqtg)	e1:SetOperation(c12593471.eqop)	c:RegisterEffect(e1)	--Search	local e2=Effect.CreateEffect(c)	e2:SetCategory(CATEGORY_TOHAND+CATEGORY_SEARCH)	e2:SetType(EFFECT_TYPE_SINGLE+EFFECT_TYPE_TRIGGER_O)	e2:SetCode(EVENT_TO_GRAVE)	e2:SetProperty(EFFECT_FLAG_DAMAGE_STEP+EFFECT_FLAG_DELAY)       e2:SetCountLimit(1,12593471)	e2:SetCondition(c12593471.thcon)	e2:SetTarget(c12593471.thtg)	e2:SetOperation(c12593471.thop)	c:RegisterEffect(e2)	--to hand	local e3=Effect.CreateEffect(c)	e3:SetDescription(aux.Stringid(12593471,1))	e3:SetCategory(CATEGORY_TOHAND)	e3:SetType(EFFECT_TYPE_IGNITION)	e3:SetRange(LOCATION_GRAVE)	e3:SetCost(c12593471.thcost2)	e3:SetTarget(c12593471.thtg2)	e3:SetOperation(c12593471.thop2)	c:RegisterEffect(e3)endfunction c12593471.eqcon(e,tp,eg,ep,ev,re,r,rp)	return Duel.GetCurrentPhase()~=PHASE_DAMAGE or not Duel.IsDamageCalculated()endfunction c12593471.filter(c)	return c:IsFaceup() and c:IsSetCard(0x2F1) or c:IsSetCard(0x2EF)endfunction c12593471.eqtg(e,tp,eg,ep,ev,re,r,rp,chk,chkc)	if chkc then return chkc:IsLocation(LOCATION_MZONE) and chkc:IsControler(tp) and c12593471.filter(chkc) end	if chk==0 then return Duel.GetLocationCount(tp,LOCATION_SZONE)>0		and Duel.IsExistingTarget(c12593471.filter,tp,LOCATION_MZONE,0,1,e:GetHandler()) end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_EQUIP)	Duel.SelectTarget(tp,c12593471.filter,tp,LOCATION_MZONE,0,1,1,e:GetHandler())	Duel.SetOperationInfo(0,CATEGORY_EQUIP,e:GetHandler(),1,0,0)endfunction c12593471.eqop(e,tp,eg,ep,ev,re,r,rp)	local c=e:GetHandler()	if not c:IsRelateToEffect(e) then return end	if c:IsLocation(LOCATION_MZONE) and c:IsFacedown() then return end	local tc=Duel.GetFirstTarget()	if Duel.GetLocationCount(tp,LOCATION_SZONE)<=0 or tc:GetControler()~=tp or tc:IsFacedown() or not tc:IsRelateToEffect(e) then		Duel.SendtoGrave(c,REASON_EFFECT)		return	end	Duel.Equip(tp,c,tc)	local e1=Effect.CreateEffect(c)	e1:SetType(EFFECT_TYPE_SINGLE)	e1:SetCode(EFFECT_EQUIP_LIMIT)	e1:SetProperty(EFFECT_FLAG_CANNOT_DISABLE)	e1:SetReset(RESET_EVENT+RESETS_STANDARD)	e1:SetLabelObject(tc)	e1:SetValue(c12593471.eqlimit)	c:RegisterEffect(e1)	local e2=Effect.CreateEffect(c)	e2:SetType(EFFECT_TYPE_EQUIP)	e2:SetCode(EFFECT_UPDATE_ATTACK)	e2:SetValue(1000)	e2:SetReset(RESET_EVENT+RESETS_STANDARD)	c:RegisterEffect(e2)endfunction c12593471.eqlimit(e,c)	return c==e:GetLabelObject()endfunction c12593471.thcon(e,tp,eg,ep,ev,re,r,rp)	return e:GetHandler():IsPreviousLocation(LOCATION_ONFIELD)endfunction c12593471.thfilter(c)	return c:IsSetCard(0x2F1) and c:IsAbleToHand()endfunction c12593471.thtg(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.IsExistingMatchingCard(c12593471.thfilter,tp,LOCATION_DECK,0,1,nil) end	Duel.SetOperationInfo(0,CATEGORY_TOHAND,nil,1,tp,LOCATION_DECK)endfunction c12593471.thop(e,tp,eg,ep,ev,re,r,rp)	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_ATOHAND)	local g=Duel.SelectMatchingCard(tp,c12593471.thfilter,tp,LOCATION_DECK,0,1,1,nil)	if g:GetCount()>0 then		Duel.SendtoHand(g,nil,REASON_EFFECT)		Duel.ConfirmCards(1-tp,g)	endendfunction c12593471.thcfilter(c)	return c:IsType(TYPE_MONSTER) and c:IsSetCard(0x2EF) and c:IsAbleToGraveAsCost()endfunction c12593471.thcost2(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.IsExistingMatchingCard(c12593471.thcfilter,tp,LOCATION_DECK+LOCATION_HAND,0,1,nil) end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_TOGRAVE)	local g=Duel.SelectMatchingCard(tp,c12593471.thcfilter,tp,LOCATION_DECK+LOCATION_HAND,0,1,1,nil)	Duel.SendtoGrave(g,REASON_COST)endfunction c12593471.thtg2(e,tp,eg,ep,ev,re,r,rp,chk)	local c=e:GetHandler()	if chk==0 then return c:IsAbleToHand() end	Duel.SetOperationInfo(0,CATEGORY_TOHAND,c,1,0,0)endfunction c12593471.thop2(e,tp,eg,ep,ev,re,r,rp)	local c=e:GetHandler()	if c:IsRelateToEffect(e) then		Duel.SendtoHand(c,nil,REASON_EFFECT)	endend