--Jovial Fowler (scripter: Elmaâœ“)function c12593473.initial_effect(c)       --special summon	local e0=Effect.CreateEffect(c)	e0:SetType(EFFECT_TYPE_FIELD)	e0:SetCode(EFFECT_SPSUMMON_PROC)	e0:SetProperty(EFFECT_FLAG_UNCOPYABLE)	e0:SetRange(LOCATION_HAND)	e0:SetCondition(c12593473.spcon)	c:RegisterEffect(e0)	--search	local e1=Effect.CreateEffect(c)	e1:SetDescription(aux.Stringid(12593473,0))	e1:SetCategory(CATEGORY_TOHAND+CATEGORY_SEARCH)	e1:SetType(EFFECT_TYPE_SINGLE+EFFECT_TYPE_TRIGGER_O)	e1:SetCode(EVENT_SPSUMMON_SUCCESS)	e1:SetProperty(EFFECT_FLAG_DAMAGE_STEP+EFFECT_FLAG_DELAY)	e1:SetCountLimit(1,12593473)	e1:SetTarget(c12593473.thtg1)	e1:SetOperation(c12593473.tgop1)	c:RegisterEffect(e1)	local e2=e1:Clone()	e2:SetDescription(aux.Stringid(12593473,1))	e2:SetCode(EVENT_TO_GRAVE)	e2:SetTarget(c12593473.thtg2)	e2:SetOperation(c12593473.tgop2)	c:RegisterEffect(e2)endfunction c12593473.filter(c)	return c:IsFaceup() and c:IsSetCard(0x2EF) and c:GetCode()~=12593473endfunction c12593473.spcon(e,c)	if c==nil then return true end	return Duel.GetLocationCount(c:GetControler(),LOCATION_MZONE)>0 and		Duel.IsExistingMatchingCard(c12593473.filter,c:GetControler(),LOCATION_MZONE,0,1,nil)endfunction c12593473.thfilter1(c)	return c:IsSetCard(0x2EF) and c:IsType(TYPE_QUICKPLAY) and c:IsAbleToHand()endfunction c12593473.thtg1(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.IsExistingMatchingCard(c12593473.thfilter1,tp,LOCATION_DECK,0,1,nil) end	Duel.SetOperationInfo(0,CATEGORY_TOHAND,nil,1,tp,LOCATION_DECK)	Duel.Hint(HINT_OPSELECTED,1-tp,e:GetDescription())endfunction c12593473.tgop1(e,tp,eg,ep,ev,re,r,rp)	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_ATOHAND)	local g=Duel.SelectMatchingCard(tp,c12593473.thfilter1,tp,LOCATION_DECK,0,1,1,nil)	if g:GetCount()>0 then		Duel.SendtoHand(g,nil,REASON_EFFECT)		Duel.ConfirmCards(1-tp,g)	endendfunction c12593473.thfilter2(c)	return c:IsSetCard(0x2EF) and c:IsType(TYPE_MONSTER) and not c:IsCode(12593473) and c:IsAbleToHand()endfunction c12593473.thtg2(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.IsExistingMatchingCard(c12593473.thfilter2,tp,LOCATION_DECK,0,1,nil) end	Duel.SetOperationInfo(0,CATEGORY_TOHAND,nil,1,tp,LOCATION_DECK)	Duel.Hint(HINT_OPSELECTED,1-tp,e:GetDescription())endfunction c12593473.tgop2(e,tp,eg,ep,ev,re,r,rp)	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_ATOHAND)	local g=Duel.SelectMatchingCard(tp,c12593473.thfilter2,tp,LOCATION_DECK,0,1,1,nil)	if g:GetCount()>0 then		Duel.SendtoHand(g,nil,REASON_EFFECT)		Duel.ConfirmCards(1-tp,g)	endend