--Frightertainer Primadonna (scripter: Elmaâœ“)function c12593477.initial_effect(c)	--spsummon	local e3=aux.AddRitualProcEqual2(c,c12593477.filter,nil,nil,c12593477.mfilter)	e3:SetDescription(aux.Stringid(12593477,1))	e3:SetType(EFFECT_TYPE_IGNITION)	e3:SetCode(0)	e3:SetCountLimit(1,12593477)	e3:SetRange(LOCATION_MZONE+LOCATION_HAND)	e3:SetCost(c12593477.cost)endfunction c12593477.filter(c)	return c:IsSetCard(0x2F1)endfunction c12593477.mfilter(c,e,tp,chk)	return not chk or c~=e:GetHandler()endfunction c12593477.cost(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return e:GetHandler():IsReleasable() end	Duel.Release(e:GetHandler(),REASON_COST)end