--frightmare puppeteer (scripter : Elmaâœ“)function c12593481.initial_effect(c)	c:EnableReviveLimit()	aux.AddXyzProcedure(c,aux.FilterBoolFunction(Card.IsSetCard,0x2EF),3,2)	--material	local e3=Effect.CreateEffect(c)	e3:SetType(EFFECT_TYPE_IGNITION)	e3:SetDescription(aux.Stringid(12593481,0))	e3:SetRange(LOCATION_MZONE)	e3:SetCountLimit(1)	e3:SetTarget(c12593481.mttg)	e3:SetOperation(c12593481.mtop)	c:RegisterEffect(e3)	--spsummon	local e1=Effect.CreateEffect(c)	e1:SetType(EFFECT_TYPE_QUICK_O)	e1:SetDescription(aux.Stringid(12593481,1))	e1:SetCategory(CATEGORY_SPECIAL_SUMMON)	e1:SetRange(LOCATION_MZONE)	e1:SetCode(EVENT_FREE_CHAIN)	e1:SetHintTiming(0,TIMINGS_CHECK_MONSTER)	e1:SetCountLimit(1,12593481)	e1:SetCost(c12593481.spcost)	e1:SetTarget(c12593481.sptg)	e1:SetOperation(c12593481.spop)	c:RegisterEffect(e1)	--cannot be target	local e4=Effect.CreateEffect(c)	e4:SetType(EFFECT_TYPE_SINGLE)	e4:SetProperty(EFFECT_FLAG_SINGLE_RANGE)	e4:SetCode(EFFECT_CANNOT_BE_BATTLE_TARGET)	e4:SetRange(LOCATION_MZONE)	e4:SetCondition(c12593481.tgcon)	e4:SetValue(aux.imval1)	c:RegisterEffect(e4)	local e5=e4:Clone()	e5:SetCode(EFFECT_CANNOT_BE_EFFECT_TARGET)	e5:SetValue(1)	c:RegisterEffect(e5)endfunction c12593481.mtfilter(c)	return c:IsSetCard(0x2EF) and c:IsCanOverlay()endfunction c12593481.mttg(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return e:GetHandler():IsType(TYPE_XYZ)		and Duel.IsExistingMatchingCard(c12593481.mtfilter,tp,LOCATION_HAND+LOCATION_REMOVED,0,1,nil) endendfunction c12593481.mtop(e,tp,eg,ep,ev,re,r,rp)	local c=e:GetHandler()	if not c:IsRelateToEffect(e) or c:IsFacedown() then return end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_XMATERIAL)	local g=Duel.SelectMatchingCard(tp,c12593481.mtfilter,tp,LOCATION_HAND+LOCATION_REMOVED,0,1,1,nil)	if g:GetCount()>0 then		Duel.Overlay(c,g)	endendfunction c12593481.spcost(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return e:GetHandler():GetAttackAnnouncedCount()==0		and e:GetHandler():CheckRemoveOverlayCard(tp,1,REASON_COST) end	local e1=Effect.CreateEffect(e:GetHandler())	e1:SetType(EFFECT_TYPE_SINGLE)	e1:SetCode(EFFECT_CANNOT_ATTACK)	e1:SetProperty(EFFECT_FLAG_CANNOT_DISABLE+EFFECT_FLAG_OATH)	e1:SetReset(RESET_EVENT+RESETS_STANDARD+RESET_PHASE+PHASE_END)	e:GetHandler():RegisterEffect(e1)	e:GetHandler():RemoveOverlayCard(tp,1,1,REASON_COST)endfunction c12593481.spfilter(c,e,tp)	return c:IsSetCard(0x2F1) and c:IsCanBeSpecialSummoned(e,0,tp,false,false)endfunction c12593481.sptg(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.GetLocationCount(tp,LOCATION_MZONE)>0		and Duel.IsExistingMatchingCard(c12593481.spfilter,tp,LOCATION_GRAVE,0,1,nil,e,tp) end	Duel.SetOperationInfo(0,CATEGORY_SPECIAL_SUMMON,nil,1,tp,LOCATION_GRAVE)endfunction c12593481.spop(e,tp,eg,ep,ev,re,r,rp)	if Duel.GetLocationCount(tp,LOCATION_MZONE)<=0 then return end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_SPSUMMON)	local g=Duel.SelectMatchingCard(tp,c12593481.spfilter,tp,LOCATION_GRAVE,0,1,1,nil,e,tp)	local tc=g:GetFirst()	if tc and Duel.SpecialSummonStep(g:GetFirst(),0,tp,tp,false,false,POS_FACEUP) then		local e1=Effect.CreateEffect(e:GetHandler())		e1:SetType(EFFECT_TYPE_SINGLE)		e1:SetCode(EFFECT_SET_ATTACK)		e1:SetValue(1100)		e1:SetReset(RESET_EVENT+RESETS_STANDARD)		tc:RegisterEffect(e1)	end	Duel.SpecialSummonComplete()endfunction c12593481.tgfilter(c)	return c:IsFaceup() and c:IsSetCard(0x2F1)endfunction c12593481.tgcon(e)	return Duel.IsExistingMatchingCard(c12593481.tgfilter,e:GetHandlerPlayer(),LOCATION_MZONE,0,1,e:GetHandler())end