--card owner: saikufunction c22446600.initial_effect(c)	aux.EnablePendulumAttribute(c)	--change scale	local e1=Effect.CreateEffect(c)	e1:SetDescription(aux.Stringid(22446600,0))	e1:SetCategory(CATEGORY_TOEXTRA)	e1:SetType(EFFECT_TYPE_IGNITION)	e1:SetRange(LOCATION_PZONE)	e1:SetCountLimit(1,22446601)	e1:SetTarget(c22446600.sctg)	e1:SetOperation(c22446600.scop)	c:RegisterEffect(e1)	--special summon	local e3=Effect.CreateEffect(c)	e3:SetDescription(aux.Stringid(22446600,0))	e3:SetCategory(CATEGORY_SPECIAL_SUMMON)	e3:SetType(EFFECT_TYPE_IGNITION)	e3:SetRange(LOCATION_MZONE)	e3:SetCountLimit(1)	e3:SetTarget(c22446600.sptg)	e3:SetOperation(c22446600.spop)	c:RegisterEffect(e3)endfunction c22446600.scfilter(c,pc)	return c:IsType(TYPE_PENDULUM) and c:IsSetCard(0x158B)		andc:GetLeftScale()~=pc:GetLeftScale()endfunctionc22446600.sctg(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.IsExistingMatchingCard(c22446600.scfilter,tp,LOCATION_DECK,0,1,nil,e:GetHandler()) end	Duel.SetOperationInfo(0,CATEGORY_TOEXTRA,nil,1,tp,LOCATION_DECK)endfunction c22446600.scop(e,tp,eg,ep,ev,re,r,rp)	local c=e:GetHandler()	if not c:IsRelateToEffect(e) then return end	Duel.Hint(HINT_SELECTMSG,tp,aux.Stringid(22446600,1))	localg=Duel.SelectMatchingCard(tp,c22446600.scfilter,tp,LOCATION_DECK,0,1,1,nil,c)	local tc=g:GetFirst()	if tc and Duel.SendtoExtraP(tc,tp,REASON_EFFECT)>0 then		local e1=Effect.CreateEffect(c)		e1:SetType(EFFECT_TYPE_SINGLE)		e1:SetCode(EFFECT_CHANGE_LSCALE)		e1:SetValue(tc:GetLeftScale())		e1:SetReset(RESET_EVENT+RESETS_STANDARD+RESET_PHASE+PHASE_END)		c:RegisterEffect(e1)		local e2=e1:Clone()		e2:SetCode(EFFECT_CHANGE_RSCALE)		e2:SetValue(tc:GetRightScale())		c:RegisterEffect(e2)	endendfunction c22446600.filter(c,e,tp)	return c:IsSetCard(0x158B) and c:IsLevelBelow(5) and c:IsCanBeSpecialSummoned(e,0,tp,false,false)endfunctionc22446600.sptg(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then return Duel.GetLocationCount(tp,LOCATION_MZONE)>0		andDuel.IsExistingMatchingCard(c22446600.filter,tp,LOCATION_HAND,0,1,nil,e,tp) end	Duel.SetOperationInfo(0,CATEGORY_SPECIAL_SUMMON,nil,1,tp,LOCATION_HAND)endfunction c22446600.spop(e,tp,eg,ep,ev,re,r,rp)	ifDuel.GetLocationCount(tp,LOCATION_MZONE)<=0 then return end	Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_SPSUMMON)	localg=Duel.SelectMatchingCard(tp,c22446600.filter,tp,LOCATION_HAND,0,1,1,nil,e,tp)	if g:GetCount()>0 then		Duel.SpecialSummon(g,0,tp,tp,false,false,POS_FACEUP)	endend