--card owner: saikufunction c22446602.initial_effect(c)	--pendulum summon	aux.EnablePendulumAttribute(c)	--fusion (p)	local e1=Effect.CreateEffect(c)	e1:SetDescription(aux.Stringid(22446602,0))	e1:SetCategory(CATEGORY_SPECIAL_SUMMON+CATEGORY_FUSION_SUMMON)	e1:SetType(EFFECT_TYPE_IGNITION)	e1:SetRange(LOCATION_PZONE)	e1:SetCountLimit(1)	e1:SetTarget(c22446602.pftg)	e1:SetOperation(c22446602.pfop)	c:RegisterEffect(e1)	--lvchange	local e3=Effect.CreateEffect(c)	e3:SetType(EFFECT_TYPE_SINGLE)	e3:SetCode(EFFECT_CHANGE_LEVEL)	e3:SetProperty(EFFECT_FLAG_SINGLE_RANGE)	e3:SetRange(LOCATION_MZONE)	e3:SetCondition(c22446602.lvcon)	e3:SetValue(3)	c:RegisterEffect(e3)endfunction c22446602.pffilter1(c,e)	return not c:IsImmuneToEffect(e)endfunction c22446602.pffilter2(c,e,tp,m,f,chkf)	return c:IsType(TYPE_FUSION) and c:IsSetCard(0x158B) and (not f or f(c))		and c:IsCanBeSpecialSummoned(e,SUMMON_TYPE_FUSION,tp,false,false) and c:CheckFusionMaterial(m,nil,chkf)endfunction c22446602.pftg(e,tp,eg,ep,ev,re,r,rp,chk)	if chk==0 then		local chkf=tp		local mg1=Duel.GetFusionMaterial(tp)		local res=Duel.IsExistingMatchingCard(c22446602.pffilter2,tp,LOCATION_EXTRA,0,1,nil,e,tp,mg1,nil,chkf)		if not res then			local ce=Duel.GetChainMaterial(tp)			if ce~=nil then				local fgroup=ce:GetTarget()				local mg2=fgroup(ce,e,tp)				local mf=ce:GetValue()				res=Duel.IsExistingMatchingCard(c22446602.pffilter2,tp,LOCATION_EXTRA,0,1,nil,e,tp,mg2,mf,chkf)			end		end		return res	end	Duel.SetOperationInfo(0,CATEGORY_SPECIAL_SUMMON,nil,1,tp,LOCATION_EXTRA)endfunction c22446602.pfop(e,tp,eg,ep,ev,re,r,rp)	if not e:GetHandler():IsRelateToEffect(e) then return end	local chkf=tp	local mg1=Duel.GetFusionMaterial(tp):Filter(c22446602.pffilter1,nil,e)	local sg1=Duel.GetMatchingGroup(c22446602.pffilter2,tp,LOCATION_EXTRA,0,nil,e,tp,mg1,nil,chkf)	local mg2=nil	local sg2=nil	local ce=Duel.GetChainMaterial(tp)	if ce~=nil then		local fgroup=ce:GetTarget()		mg2=fgroup(ce,e,tp)		local mf=ce:GetValue()		sg2=Duel.GetMatchingGroup(c22446602.pffilter2,tp,LOCATION_EXTRA,0,nil,e,tp,mg2,mf,chkf)	end	if sg1:GetCount()>0 or (sg2~=nil and sg2:GetCount()>0) then		local sg=sg1:Clone()		if sg2 then sg:Merge(sg2) end		Duel.Hint(HINT_SELECTMSG,tp,HINTMSG_SPSUMMON)		local tg=sg:Select(tp,1,1,nil)		local tc=tg:GetFirst()		if sg1:IsContains(tc) and (sg2==nil or not sg2:IsContains(tc) or not Duel.SelectYesNo(tp,ce:GetDescription())) then			local mat1=Duel.SelectFusionMaterial(tp,tc,mg1,nil,chkf)			tc:SetMaterial(mat1)			Duel.SendtoGrave(mat1,REASON_EFFECT+REASON_MATERIAL+REASON_FUSION)			Duel.BreakEffect()			Duel.SpecialSummon(tc,SUMMON_TYPE_FUSION,tp,tp,false,false,POS_FACEUP)		else			local mat2=Duel.SelectFusionMaterial(tp,tc,mg2,nil,chkf)			local fop=ce:GetOperation()			fop(ce,e,tp,tc,mat2)		end		tc:CompleteProcedure()	endendfunction c22446602.lvcon(e)	return e:GetHandler():IsDefensePos()end